import styled from 'styled-component'

export const Container = styled.div`
  display: grid;
  grid-template-columns: 5fr 1fr;
`

export const Input = styled.input`
  border: 1px solid transparent;
  border-radius: 21px;
`

export const Button = styled.button`
  border: 1px solid transparent;
  border-radius: 21px;
  background: #652af5;
  font-size: 14em;
`
